$(document).ready(function () {
    $('.top-slider__slides').fractionSlider({
        'slideTransition': 'fade',
        'slideTransitionSpeed' : 100,
        'transitionIn' : 'fade',
        'transitionOut' : 'fade',
        'timeout': 8000,
        'speedIn' : 2000,
        'delay': 300,
        'controls': true,
        'pauseOnHover': true
    });

    var bg = 'url('+$('.active-slide .top-slider__slide-bg').attr('data-pic')+')';
    $('.top-slider').css('background-image', bg);

    $('.personal__slider').bxSlider({
        pager: true,
        pagerSelector: '.personal-slider',
        pagerCustom: '.personal-tabs',
        nextText: '',
        prevText: '',
        onSliderLoad: function(){
            $('.personal-tab').css('display','block');
        }
    });

    $('.index-product__link--video').fancybox({
        'width'				: '90%',
        'height'			: '100%',
        'autoScale'     	: true,
        'transitionIn'		: 'none',
        'transitionOut'		: 'none',
        'type'				: 'iframe',
        'scrolling'		: 'no'
    });

    $('.index-projects__select').selectbox();

    $('.page-header__menu-btn').click(function () {
        $('.main-menu').slideToggle();
    });

    $('.scopes-tab__item').click(function () {
        var pic = $(this).attr("data-pic");

        $('.scopes-tab__item').removeClass('scopes-tab__item--active');
        $(this).addClass('scopes-tab__item--active');

        $('.scopes-tab__pic img').attr("src", pic);
    })

    $('.button-up a').click(function () {
        $('.main-menu__link').removeClass('main-menu__link--active');
    });
});
function g_map ()
{
    var maps = $('.contacts__map');

    maps.each(function() {
        var current_map = $(this);
        var latlng = new google.maps.LatLng(current_map.attr('data-longitude'), current_map.attr('data-latitude'));
        //var latlngmarker = new google.maps.LatLng(current_map.attr('data-marker-longitude'), current_map.attr('data-marker-latitude'));
        var point = current_map.attr('data-marker');

        var myOptions = {
            zoom: 15,
            center: latlng,
            mapTypeId: google.maps.MapTypeId.ROADMAP,
            mapTypeControl: false,
            scrollwheel: false,
            draggable: true,
            panControl: true,
            zoomControl: true,
            disableDefaultUI: false
        };

        var stylez = [
            {
                featureType: "all",
                elementType: "all",
                stylers: [
                    { saturation: -100 } // <-- THIS
                ]
            }
        ];

        var map = new google.maps.Map(current_map[0], myOptions);

        /*var mapType = new google.maps.StyledMapType(stylez, { name:"Grayscale" });
        map.mapTypes.set('Grayscale', mapType);
        map.setMapTypeId('Grayscale');*/

        var marker = new google.maps.Marker({
            map: map,
            icon: {
                size: new google.maps.Size(208,102),
                origin: new google.maps.Point(0,0),
                anchor: new google.maps.Point(-150,0),
                url: point
            },
            position: latlng
        });



        google.maps.event.addDomListener(window, "resize", function() {
            var center = map.getCenter();
            google.maps.event.trigger(map, "resize");
            map.setCenter(center);
        });
    });
};

google.maps.event.addDomListener(window, 'load', g_map);
